import React from "react";
import NoCover from "./../assets/images/NoCover.png";
import axios from 'axios'
import { useState } from "react";


function BookCard({ data, isbn, addEnable, haveRead }) {
    const [display, setDisplay] = useState();
    const [visible, setVisible] = useState(true);
    const [notes,setNotes] =useState();
    const {
        title,
        authors,
        description,
        canonicalVolumeLink,
        // imageLinks,
    } = data;

    // const thumbnail=imageLinks.thumbnail;
   function handleNotes(event){
    const books = event.target.value;
    setBook(books);
   }

    function handleSubmit(event) {
        event.preventDefault();
        if (addEnable) {
            axios.post('/addBook', {
                isbn: isbn,
                title: title,
                authors: authors[0],
            }).then((data) => {
                if (data.status = "200") {
                    setDisplay("Added successfully")
                    console.log("I'm here in")
                }
                console.log("Added book,", data);

            }).catch(() => { 
                console.log("Added book in catch", data);
                setVisible(false); })
        }
        else {
            axios.get('/removeBook/' + isbn).then((response) => {

            }).then((response) => {
                console.log("Deleted book", response);
                setDisplay("removed successfully");
            
            }).catch(() => { setVisible(false); })

        }


    }
    return (
      setVisible?  <div>
      <form onSubmit={handleSubmit}>
          {/* <div >
          <img
              src={
                thumbnail
              }
              alt={title}
          />
      </div> */}
          <h2>Title : {title}</h2>
          <h2>ISBN : {isbn}</h2>
          <h2>Author:</h2>
          <h4>
              {authors}
          </h4>
          <h2>Description:</h2>
          <p>
              {description}
          </p>
          <h2>Have read:</h2>
          <p>
              {haveRead}
          </p>
          <div className="form-group">
              <label for="notes">Notes:</label>
              <textarea className="form-control" rows="5" id="notes" onChange={handleNotes}></textarea>
          </div>
          {addEnable ? (<div className="form-group">
              <label for="add"></label>
              <button type="submit" className="btn btn-danger" rows="5" id="add">Add</button>
          </div>) : (<div className="form-group">
              <label for="delete"></label>
              <button type="submit" className="btn btn-danger" rows="5" id="delete">Delete</button>
          </div>)}
      </form>
  </div> : null
    );
}

export default BookCard;
