import { React, useState } from "react";
import "./App.css";
import axios from 'axios'
import BooksContainer from "./containers/BooksContainer/BooksContainer";
import BookCard from "./components/BookCard";

function App() {
    const [books, setBook] = useState("");
    const [result, setResult] = useState([]);
    const [apiKey, setApiKey] = useState('AIzaSyDwbT0GYox6t9Gnw1IQiaJHeg6bgQUGXI4')
    const [haveRead, setHaveRead] = useState("")

    function handleChange(event) {
        const books = event.target.value;
        setBook(books)
    }

    function handleSubmit(event) {
        event.preventDefault();
        console.log(typeof(books));
        axios.post( '/addBook/'+books).then((data)=>{
        setResult(data.data.items);
        console.log(data.data.items);
        }).catch(() =>{
            axios.get( 'https://www.googleapis.com/books/v1/volumes?q=isbn:' +books).then((data)=>{
                setResult(data.data.items);
                console.log(data.data.items);
                }).then((data)=>{
                    axios.get( 'https://www.googleapis.com/books/v1/users/104692358047678426117/bookshelves/4/volumes').then((data)=>{
                        setHaveRead(data.data.items);
                        console.log("I am in the second level",data.data.items);
                        })
                })
        })
    }

    return (
        <div className="container">
            <h1>Book Search App</h1>
            <form onSubmit={handleSubmit}>
                <div className="form-group">
                    <input
                        type="text"
                        className="form-control mt-20"
                        placeholder="Search books"
                        autoComplete="off"
                        onChange={handleChange} />
                </div>
                <button type="submit" className="btn btn-danger">Search</button>
            </form>
            <div>
                {
                    (result&& (
                        <div>
                            {result.map((result, index) => (
                                <BookCard key={index} data={result.volumeInfo} isbn={books} />
                            ))}{" "}
                        </div>
                    ))}
            </div>

        </div>
    );
}

export default App;
