import React from "react";
import NoCover from "./../assets/images/NoCover.png";
import axios from 'axios'
import { useState } from "react";


function BookCard({ data, isbn, addEnable, haveRead }) {
    const [display, setDisplay] = useState();
    const [visible, setVisible] = useState(true);
    const [notes,setNotes] =useState();
    const {
        title,
        authors,
        description,
        canonicalVolumeLink,
        pageCount,
        imageLinks,
        thumbnail
    } = data;

    
   function handleNotes(event){
    const notes = event.target.value;
    setNotes(notes);
   }

    function handleSubmit(event) {
        event.preventDefault();
        if (addEnable) {
            axios.post('/addBook', {
                isbn: isbn,
                title: title,
                authors: authors[0],
                pageCount:pageCount,
                haveRead: haveRead,
                notes:notes,
                thumbnail:imageLinks.thumbnail

            }).then((data) => {
                if (data.status = "200") {
                    setDisplay("Added successfully")
                    console.log("I'm here in")
                }
                console.log("Added book,", data);

            }).catch(() => { 
                console.log("Added book in catch", data);
                setVisible(false); })
        }
        else {
            axios.get('/removeBook/' + isbn).then((response) => {

            }).then((response) => {
                console.log("Deleted book", response);
                setDisplay("removed successfully");
            
            }).catch(() => { setVisible(false); })

        }


    }
    return (
      setVisible?  <div className="flex gap-5 flex-wrap justify-center  py-5">
        <div className="flex w-[200px] border border-gray-500  
           bg-gray-900 p-3 flex-col items-center">
      <form onSubmit={handleSubmit}>
        
      {addEnable?  <div ><img 
              src={
                imageLinks.thumbnail
              }
              alt={title}
              className="w-24 mb-4 border-4  border-teal-400 rounded-full"
          /></div> :
          <div ><img 
              src={
                thumbnail
              }
              alt={title}
              className="w-24 mb-4 border-4  border-teal-400 rounded-full"
          /></div>
          }    
      
          <h4 className="text-xl">Title : {title} </h4>
          
          <h4>ISBN :  {isbn}</h4>
          <h4>Author: {authors}</h4>
        
          <h4>Page Count : {pageCount}</h4>
        
          <h4>Have read: {haveRead}</h4>
       
          <div className="form-group">
              <label for="notes">Notes:</label>
              <textarea className="form-control" value={notes} rows="5" id="notes" onChange={handleNotes}></textarea>
          </div>
          {addEnable ? (<div className="form-group">
              <label for="add"></label>
              <button type="submit" className="btn btn-danger" rows="5" id="add">Add</button>
          </div>) : (<div className="form-group">
              <label for="delete"></label>
              <button type="submit" className="btn btn-danger" rows="5" id="delete">Delete</button>
          </div>)}
      </form>
  </div>
  </div> : null
    );
}

export default BookCard;
