import { React, useEffect, useState } from "react";
import "./App.css";
import axios from 'axios'
import BooksContainer from "./containers/BooksContainer/BooksContainer";
import BookCard from "./components/BookCard";

function App() {
    const [books, setBook] = useState("");
    const [result, setResult] = useState();
    const [apiKey, setApiKey] = useState('AIzaSyDwbT0GYox6t9Gnw1IQiaJHeg6bgQUGXI4')
    const [haveRead, setHaveRead] = useState("")

    function handleChange(event) {
        const books = event.target.value;
        setBook(books)
    }

    function handleSubmit(event) {
        event.preventDefault();
        console.log(typeof (books));

        axios.get('http://localhost:9090/findBookByIsbn/' + books).then((response) => {
            setResult(response.data);
            console.log(response);
            console.log(typeof (response.data));

        })

   {  result ?  ( axios.get('https://www.googleapis.com/books/v1/volumes?q=isbn:' + books).then((response) => {
            setResult(response.data.items);
            console.log(response.data.items);
            console.log(typeof (response.data.items[0]));
        }).then((response) => {
            axios.get('https://www.googleapis.com/books/v1/users/110459311993195018931/bookshelves/4/volumes').then((response) => {
                setHaveRead(response.data.items);
                console.log("I am in the second level", response.data.items);
            })
        }) ) :null}

    }





    return (
        <div className="container">
            <h1>Book Search App</h1>
            <form onSubmit={handleSubmit}>
                <div className="form-group">
                    <input
                        type="text"
                        className="form-control mt-20"
                        placeholder="Search books"
                        autoComplete="off"
                        onChange={handleChange} />
                </div>
                <button type="submit" className="btn btn-danger">Search</button>
            </form>
            <div >
                {(!result && (
                    <p>Sorry, no result were found for your query.</p>
                )) ||
                    (result && (
                        <div>

                            <BookCard data={result.volumeInfo} isbn={books} />

                        </div>
                    ))}
            </div>

        </div>
    );
}

export default App;
