import React from "react";
import NoCover from "./../assets/images/NoCover.png";

function BookCard({ data }) {
    const {
        title,
        authors,
        thumbnail,
        description,
        canonicalVolumeLink,
        imageLinks,
    } = data;
    return (
        <div>
            <div >
                <img
                    src={
                        (!imageLinks && NoCover) ||
                        (imageLinks.thumbnail && imageLinks.thumbnail)
                    }
                    alt={title}
                />
            </div>
            <h2>Title : {title}</h2>
            <h2>Author:</h2>
            <h4>
                {(!authors && "Author Unknown") ||
                    (authors && authors.join(", "))}
            </h4>
            <h2>Description:</h2>
            <p>
                {(!description && "No description has been provided.") ||
                    (description && description)}
            </p>

            <div class="btn-group">

                <label class="btn btn-secondary" for="read">Read</label>
                <input type="radio" className="btn-check" name="options" id="read" autocomplete="off" checked />
            </div>
            <div class="btn-group">

                <label class="btn btn-secondary" for="notRead">Not Read</label>
                <input type="radio" className="btn-check" name="options" id="notRead" autocomplete="off" />
            </div>
            <div class="form-group">
                <label for="comment">Comment:</label>
                <textarea class="form-control" rows="5" id="comment"></textarea>
            </div>



        </div>
    );
}

export default BookCard;
