import { React, useEffect, useState } from "react";
import "./App.css";
import axios from 'axios'
import BooksContainer from "./containers/BooksContainer/BooksContainer";
import BookCard from "./components/BookCard";

function App() {
    const [books, setBook] = useState("");
    const [result, setResult] = useState();
    const [haveReadList, setHaveReadList] = useState([]);
    const [fromDb, setFromDb] = useState(false)
    const [haveRead, setHaveRead] = useState("no");
    const [addEnable, setAddEnable] = useState(true)

    function handleChange(event) {
        const books = event.target.value;
        setBook(books)
    }

    function handleHaveRead(response) {
        response.map((readbook) => {
            console.log("hello", readbook.volumeInfo.industryIdentifiers[1].identifier);
            console.log("hi", books)
            readbook.volumeInfo.industryIdentifiers.map((val) => {
                if (val.type == "ISBN_13") {
                    if (val.identifier == books) {
                        setHaveRead("yes");
                        console.log(val.identifier);
                    }
                }

            })
        })
        // if (readbook.volumeInfo.industryIdentifiers[1].identifier === books) {

        //     setHaveRead("yes");

        //     console.log("if",readbook.volumeInfo.industryIdentifiers[1].identifier);
        // } })
    }

    function handleSubmit(event) {
        event.preventDefault();
        console.log(typeof (books));

        axios.get('http://localhost:9090/findBookByIsbn/' + books).then((response) => {

            console.log(response);
            console.log(typeof (response.data));
            if (response.data == "") {
                console.log("check");
                callapi()
            }

            else {
                setResult(response.data);
                setAddEnable(false);
                setFromDb(true);
            }

        })

    }

    function callapi() {
        axios.get('https://www.googleapis.com/books/v1/volumes?q=isbn:' + books).then((response) => {
            setResult(response.data.items[0]);
            setFromDb(false);
            console.log(response.data.items[0]);
            console.log(typeof (response.data.items[0].volumeInfo));
        }).then((response) => {
            axios.get('https://www.googleapis.com/books/v1/users/110459311993195018931/bookshelves/4/volumes').then((response) => {
                console.log("I am in the second level", response.data.items);
                setHaveReadList(response.data.items);
                handleHaveRead(response.data.items);

            })
        })
    }



    return (
        <div className="container">
            <h1>Book Search App</h1>
            <form onSubmit={handleSubmit}>
                <div className="form-group">
                    <input
                        type="text"
                        className="form-control mt-20"
                        placeholder="Search books"
                        autoComplete="off"
                        onChange={handleChange} />
                </div>
                <button type="submit" className="btn btn-danger">Search</button>
            </form>
            <div >
                {(!result && (
                    <p>Sorry, no result were found for your query.</p>
                )) ||
                    (result && (
                        <div>
                            {fromDb ? <BookCard data={result} isbn={books} addEnable={addEnable} haveRead={haveRead} /> :
                                <BookCard data={result.volumeInfo} isbn={books} addEnable={addEnable} haveRead={haveRead} />}
                        </div>
                    ))}
            </div>

        </div>
    );
}

export default App;
