import { React, useState } from "react";
import "./App.css";
import axios from 'axios'
import BooksContainer from "./containers/BooksContainer/BooksContainer";


function App() {
    const [book, setBook] = useState("");
    const [result, setResult] = useState([]);
    const [apiKey, setApiKey] = useState('AIzaSyDwbT0GYox6t9Gnw1IQiaJHeg6bgQUGXI4')
    function handleChange(event) {
        const book = event.target.value;
        setBook(book)
    }

    function handleSubmit(event) {
        event.preventDefault();
        axios.get(`https://www.googleapis.com/books/v1/volumes?q=`+ book +`&key=`+ apiKey).then((data)=>{
        setResult(data.data.items);
        console.log(data.data.items);
        })
    }
    return (
        <div className="container">
            <h1>Book Search App</h1>
            <form onSubmit={handleSubmit}>
                <div className="form-group">
                    <input
                        type="text"
                        className="form-control mt-20"
                        placeholder="Search books"
                        autoComplete="off"
                        onChange={handleChange} />
                </div>
                <button type="submit" className="btn btn-danger">Search</button>
            </form>

            {result.map(book =>{ 
            {book.title}
            <img src={book.volumeInfo.imageLinks.thumbnail} alt={book.title}/>})}
        </div>
    );
}

export default App;
