import React from "react";
import NoCover from "./../assets/images/NoCover.png";
import axios from 'axios'

function BookCard({ data,isbn,addEnable }) {
    const {
        title,
        authors,
        description,
        canonicalVolumeLink,
        imageLinks,
    } = data;

const thumbnail=imageLinks.thumbnail;

    function handleSubmit(event) {
        event.preventDefault();
        axios.post('/addBook/'+isbn,{
            title: title,
            authors:authors[0],
          }).then((data)=>{
        console.log("Added book");
        })
    }
    return (
        <div>
            <form onSubmit={handleSubmit}>
            <div >
                <img
                    src={
                      thumbnail
                    }
                    alt={title}
                />
            </div>
            <h2>Title : {title}</h2>
            <h2>Author:</h2>
            <h4>
                    {authors}
            </h4>
            <h2>Description:</h2>
            <p>
            {description}
            </p>
            <div className="form-group">
                <label for="notes">Notes:</label>
                <textarea className="form-control" rows="5" id="notes"></textarea>
            </div>
      {   addEnable ? (<div className="form-group">
                <label for="add"></label>
                <button  type="submit" className="btn btn-danger" rows="5" id="add">Add</button>
            </div>) : (<div className="form-group">
                <label for="delete"></label>
                <button  type="submit" className="btn btn-danger" rows="5" id="delete">Delete</button>
            </div>)}
            </form>
        </div>
    );
}

export default BookCard;
